INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "National Centre of Public Health Protection (NCPHP)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_STATISTICS_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "<p><a href=\"http://ncpha.government.bg/index.php?lang=en\" target=\"_blank\">Link</a> to the institute<br /><strong>Short abstract<br /></strong></p><p>The National Center of Public Health and Analyses (NCPHA) is a structure within the national healthcare system and carries out activities for protecting public health, promoting health and preventing diseases, providing information for healthcare management.</p><p>In conformity with its main activities for the purpose of protecting and promoting health the NCPHA examines the population's health status and the relationship with environmental factors and living conditions; conducts epidemiological surveys and evaluates risk factors for chronic non-communicable diseases; participates in the development, coordination and performance of national programmes and action plans as well as in international research programmes oriented to strengthening public health (including mental, reproductive and sexual); carries out intervention activities, training workshops and publishes information and methodical materials; assesses exposure and health risk for the population from the impact of biological, chemical and physical hazards on the ambient and working environment as appropriate recommendations have been prepared;&nbsp; conducts monitoring on nutrition and nutritional status of the population and develops Manuals for nutrition of target population groups.<br /><br />See more in <a href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Bulgaria\" target=\"_blank\">OSHWiki</a></p><p>&nbsp;</p>");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id) VALUES ("MATRIX_STATISTICS", @nutsId, 0, 0, 1, false, @maxId+1, @maxId+2, null);
