INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "<p><a href=\"http://www.knsb-bg.org/index.php/deinosti1/2015-04-03-15-00-57\" target=\"_blank\">Link</a> to the institute</p><p><strong>Short abstract<br /></strong></p><p><span lang=\"EN-US\">The main areas of activity of the ISTUR are research, consultations and training in the following areas: development of trade unionism and industrial relations - historical development;&nbsp;structures;&nbsp;social partnership;&nbsp;collective bargaining;&nbsp;collective labor disputes;&nbsp;protest actions and strikes;&nbsp;an international trade union movement;&nbsp;labor market and trade union strategies;&nbsp;privatization;&nbsp;social policy - living standards, prices, income, social protection.</span></p><p>See more in <a href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Bulgaria\" target=\"_blank\">OSHWiki</a></p><p>&nbsp;</p>");
