INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "<p><strong>Short abstract<br /></strong></p><p>In Cyprus there is no particular research institute specializing on OSH. The eighth fundamental pillar of the Strategy on Safety and Health at Work for the Period 2007-2012 deals with the encouragement of scientific progress and research. The Strategy intends to promote the collaboration between universities, institutes and services in Cyprus and abroad in order to face OSH risks. Assignments of studies to suitable organizations are a means towards this end.</p><p>See more in <a href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Cyprus\" target=\"_blank\">OSHWiki</a></p><p>&nbsp;</p>");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id) VALUES ("MATRIX_STATISTICS", @nutsId, 0, 0, 1, false, @maxId+1, @maxId+2, null);
