INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "The National Research Centre for the Working Environment (NRCWE)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_STATISTICS_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "<p><a href=\"http://www.arbejdsmiljoforskning.dk/da\" target=\"_blank\">Link</a>&nbsp;to the institute</p><p><strong>Short abstract<br /></strong></p><p>(Det Nationale Forskningscenter for Arbejdsmiljø, NFA)</p><p>NRCWE is a government research institute under the auspices of the Ministry of Employment. The mission of the NRCWE is to generate new research based knowledge about occupational safety and health in order to contribute to healthy and stimulating working conditions in accordance with the demands and needs of society, enterprises and the working environment system. The NRCWE provides research based knowledge and counselling to the Ministry of Employment, including the Danish Working Environment Authority. The NRCWE disseminates research based knowledge to the social partners, enterprises and working environment counsellors, contributes to higher education at the universities, and serves as an entrance to general information about working environment issues through the Working Environment Information Centre.</p><p>The following are the current strategic research areas of the NRCWE:</p><ul><li>Psychosocial working environment</li><li>Musculoskeletal disorders</li><li>Nano safety</li></ul><p>Work accidents and safety culture</p><p>See more in&nbsp;<a href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Denmark%20\" target=\"_blank\">OSHWiki</a></p>");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id) VALUES ("MATRIX_STATISTICS", @nutsId, 0, 0, 1, false, @maxId+1, @maxId+2, null);
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+1, NULL, NULL, "MATRIX_STATISTICS_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "The Danish Working Environment Research Fund");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_STATISTICS_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "<p><a href=\"http://engelsk.arbejdstilsynet.dk/en/research/arbejdsmiljoforskningsfonden%20\">Link&nbsp;</a>to the institute</p><p><strong>Short abstract<br /></strong></p><p>The objective ofthe Danish Working Environment Research Fund is to strengthen Danish working environment research through the adoption of a research strategy and allocation of funds based on the prioritized themes laid out in the research strategy. Presently, these themes are:</p><ul><li>Work accidents</li><li>Work accidents</li><li>Psychological working environment</li><li>Musculoskeletal disorders</li></ul><p>OSH instruments The Fund supports research and development in occupational safety and health with the purpose of preventing and reducing working environment problems leading to mental and physical health problems and early retirement from the labour market. The Fund also supports reviews of occupational diseases on basis of specific calls for applications. From 2003 to 2016, the Fund has approved research grants for 284 projects totaling DKK 758,7 million. The Danish Working Environment Research Fund invites applications twice a year with deadlines around 1 March and 1 September.</p><p>See more in&nbsp;<a href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Denmark%20\" target=\"_blank\">OSHWiki</a></p>");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id) VALUES ("MATRIX_STATISTICS", @nutsId, 0, 0, 1, false, @maxId+1, @maxId+2, null);
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+1, NULL, NULL, "MATRIX_STATISTICS_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "National Institute for Health Development (Tervise Arengu Instituut)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_STATISTICS_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "<p><a href=\"http://www.tai.ee/en/%20\" target=\"_blank\">Link&nbsp;</a>to the institute</p><p><strong>Short abstract<br /></strong></p><p>The National Institute for Health Development (Tervise Arengu Instituut; NIHD)was established in 2003 as a governmental organization under the Ministry of Social Affairs of Estonia. The mission of NIHD is to promote the health of the Estonian population and enhance the quality of life through knowledge-based development and applied research activities. The main activities of the NIHD are research, development and implementation of activities in the health and social sectors.</p><p>See more in&nbsp;<a href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_–_Estonia%20\" target=\"_blank\">OSHWiki</a></p>");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id) VALUES ("MATRIX_STATISTICS", @nutsId, 0, 0, 1, false, @maxId+1, @maxId+2, null);
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+1, NULL, NULL, "MATRIX_STATISTICS_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "Tartu University (Tartu Ülikool)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_STATISTICS_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "<p><a href=\"http://www.ut.ee/en%20\" target=\"_blank\">Link&nbsp;</a>to the institute</p><p><strong>Short abstract<br /></strong></p><p>Tartu University (Tartu Ülikool)&nbsp;has the following functions relating to occupational safety and health:</p><ul type=\"square\"><li>Coordination of the occupational health (OH) teaching programme in the Faculty of Medicine (specialist training for physicians);</li><li>Organisation of OHS and surveillance of the working environment in the university and connected units (Biomedicum, Clinicum, ect.);</li><li>Leading of OH curricula development in the Department of Public Health;</li><li>Organisation and participation in the training courses of OH for other institutions and specialist groups;</li><li>Advisory role of OH expertise and programme development in the Ministry of Social Affairs</li></ul><p>See more in&nbsp;<a href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_–_Estonia\" target=\"_blank\">OSHWiki</a></p>");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id) VALUES ("MATRIX_STATISTICS", @nutsId, 0, 0, 1, false, @maxId+1, @maxId+2, null);
