INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "Institute of Occupational Safety and Environmental Health of the Riga Stradiņš University");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_STATISTICS_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "<p><a href=\"http://www.rsu.lv/ddvvi\" target=\"_blank\">Link</a>&nbsp;to the institute<br /><strong>Short abstract<br /></strong></p><p>The Institute of Occupational Safety and Environmental Health is leading research institution in the field of OSH in Latvia both in terms of experience, projects and publications. The Institute is part of the Riga Stradiņš University, which is not only a research institute but rather a combination of academic, scientific and training institution. The Institute for Occupational Safety and Environmental Health has several functions including training for undergraduate and postgraduate students on topics, concerning occupational health and safety, occupational medicine and environmental health, research and expert services. In the field of OSH research its current activities are mostly targeted to research in effects of well-established OSH risks like welding fumes, noise and vibration and also on new and emerging factors like ultra-fine particles and psychosocial and organizational risks.</p><p>See more in&nbsp;<a href=\"https://oshwiki.eu/wiki/OSH_systems_at_national_level_-_Latvia\" target=\"_blank\">OSHWiki</a></p>");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id) VALUES ("MATRIX_STATISTICS", @nutsId, 0, 0, 1, false, @maxId+1, @maxId+2, null);
