INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "Faculty of Chemistry of the Latvian University (Latvijas universitātes Ķīmijas fakultāte)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_STATISTICS_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "<p><a href=\"http://www.rsu.lv/ddvvi%20\" target=\"_blank\">Link</a>&nbsp;to the institute<br /><strong>Short abstract<br /></strong></p><p>Some research activities are also done by the Faculty of Chemistry of the Latvian University (Latvijas universitātes Ķīmijas fakultāte). Their recent research includes studies both in the area of chemical risks but also ergonomic and organizational factors of workplaces.</p><p>See more in&nbsp;<a href=\"https://oshwiki.eu/wiki/OSH_systems_at_national_level_-_Latvia\" target=\"_blank\">OSHWiki</a></p>");
