INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_STATISTICS_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "<p><a href=\"http://www.hi.lt/profesines-sveikatos-centras.html%20\" target=\"_blank\">Link</a>&nbsp;to the institute<br /><strong>Short abstract<br /></strong></p><p>Research activities of the Centre are aimed at: investigation and evaluation of the impact of working environment factors on workers’ health and functional capacity; investigation and evaluation of the preventive effectiveness of the occupational health (care); development and testing of innovative interventions on occupational health (care) in practice.</p><p>See more in&nbsp;<a href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Lithuania%20\" target=\"_blank\">OSHWiki</a></p>");
