INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "Department of Labour Safety and Fire Protection of the Vilnius Gediminas Technical University");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_STATISTICS_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "<p><a href=\"http://www.dgsk.st.vgtu.lt/%20\" target=\"_blank\">Link</a>&nbsp;to the institute<br /><strong>Short abstract<br /></strong></p><p>The VGTU is a one of the largest state higher academic schools, which strives for leading position among Baltic countries in technical and engineering education as well as the field of scientific research. The Department of Labour Safety and Fire Protection takes part in education and training of students in OSH area, research in OSH and fire safety area, preparation of OSH and fire safety regulations, etc.</p><p>See more in&nbsp;<a href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Lithuania%20\" target=\"_blank\">OSHWiki</a></p><p>&nbsp;</p>");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id) VALUES ("MATRIX_STATISTICS", @nutsId, 0, 0, 1, false, @maxId+1, @maxId+2, null);
