INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "Department of Environment and Occupational Medicine of the Lithuanian University of Health Sciences");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_STATISTICS_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "<p><a href=\"http://www.lsmuni.lt/en/structure/medical-academy-/faculty-of-public-health-/departments/%20\" target=\"_blank\">Link</a>&nbsp;to the institute<br /><strong>Short abstract<br /></strong></p><p>The LSMU is the largest institution of medical education and training in Lithuania. Medical Academy of the LSMU has five faculties: Public Health, Nursing, Pharmacy, Odontology and Medicine. Kaunas Clinics (the LSMU’s hospital) is the largest medical institution in Lithuania and one of the largest university hospitals in the Baltic countries. It is also used for teaching students and research purposes. The Department of Environment and Occupational Medicine takes part in education and training of students in OSH area, research in OSH area, preparation of OSH regulations, etc.</p><p>See more in&nbsp;<a href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Lithuania%20\" target=\"_blank\">OSHWiki</a></p><p>&nbsp;</p>");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id) VALUES ("MATRIX_STATISTICS", @nutsId, 0, 0, 1, false, @maxId+1, @maxId+2, null);
