INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "Central Institute for Labour Protection – National Research Institute (CIOP-PIB)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_STATISTICS_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "<p><a href=\"http://www.ciop.pl/\" target=\"_blank\">Link</a>&nbsp;to the institute<br /><strong>Short abstract<br /></strong></p><p>Central Institute for Labour Protection - National Research Institute (Centralny Instytut Ochrony Pracy – Państwowy Instytut Badawczy, CIOP-PIB) is the main scientific research institution in Poland that employs a comprehensive approach to improvement of working conditions according to human psychophysical abilities. The Institute’s main activity constitute research and development works, leading to new technical and organisational solutions in the area of labour protection, occupational safety, health and ergonomics as well as other tasks essential for reaching the goals of the socio-economic policy in the field. The Institute is a legally, organisationally, economically and financially independent state body.</p><p>See more in&nbsp;<a href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Poland%20\" target=\"_blank\">OSHWiki</a></p><p>&nbsp;</p><p><strong>&nbsp;</strong></p><p>&nbsp;</p>");
