INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "<p><strong>Short abstract</strong></p><p>Research in the field of safety and health at work is mainly carried out by research groups at universities and also at technology centers, which often have projects in collaboration with universities. There is a national network that encompasses the eight existing technology centers, promoting the cooperation among them</p><p>See more in&nbsp;<a href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Portugal\" target=\"_blank\">OSHWiki</a></p><p>&nbsp;</p><p><strong>&nbsp;</strong></p><p>&nbsp;</p>");
