INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "The National Research & Development Institute on Occupational Safety ‘Alexandru Darabont’ INCDPM");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_STATISTICS_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "<p><a href=\"http://www.inpm.ro/\" target=\"_blank\">Link</a>&nbsp;to the institute<br /><strong>Short abstract<br /></strong></p><p>Apart of its steady role within the occupational safety and health prevention, the National Research &amp; Development Institute on Occupational Safety ‘Alexandru Darabont’ INCDPM also constitutes the national OSH research body operating under the co-ordination of the Ministry of National Education as State Authority for Scientific Research, Technological Development and Innovation, in conformity with the Government Decision No. 185 of 16 April 2013, and the Ministry of Labour, Family, Social Protection and Elderly as well.</p><p>INCDPM develops a series of fundamental and applicative research studies meant to the improvement of the safety and health at work in line with the EU directives provisions transposed within the national legislation in force. The topics addressed by INCDPM’s research work include: risk assessment, studies on the elimination/reduction of the major workplace related risk factors (chemical and biological risks, mechanical and physical risks, MSDs, psychosocial risks, ergonomics, etc.), personal protective equipment (PPE), certification of machines and PPE.</p><p>The research works INCDPM carries out also include the development of OSH research projects and programmes aiming at improving the working conditions within all the activity sectors.</p><p>INCDPM’s R&amp;D studies contribute to a large extent to the elaboration and substantiation of the OSH policies drawn up at governmental level by the Ministry of Labour, Family, Social Protection and Elderly and the Ministry of National Education in collaboration with the social partners and the most significant OSH promoters at national level.</p><p>See more in&nbsp;<a href=\"https://oshwiki.eu/wiki/OSH_systems_at_national_level_-_Romania\" target=\"_blank\">OSHWiki</a></p><p>&nbsp;</p><p><strong>&nbsp;</strong></p><p>&nbsp;</p><p><strong>&nbsp;</strong></p><p>&nbsp;</p>");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id) VALUES ("MATRIX_STATISTICS", @nutsId, 0, 0, 1, false, @maxId+1, @maxId+2, null);
