INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "<p><a href=\"https://forte.se\" target=\"_blank\">Link&nbsp;</a>to the institute<br /><strong>Short abstract<br /></strong></p><p>Swedish Council for Working Life and Social Research (Forskninsrädet rör arbetsliv och socialvetenskap, FAS) promotes the accumulation of knowledge in matters relating to working life and the understanding of social conditions and processes through the promotion and support of research and co-operation and dissemination of information. The Council supports basic and applied research relating to working life.</p><p>See more in&nbsp;<a href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Sweden\" target=\"_blank\">OSHWiki</a></p><p><strong>&nbsp;</strong></p><p><strong>&nbsp;</strong></p><p>&nbsp;</p><p><strong>&nbsp;</strong></p><p>&nbsp;</p>");
