INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "Forestry Research Institute of Sweden (Skogforsk stiftelsen skogsbrukets forskningsinstitut)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_STATISTICS_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "<p><a href=\"http://www.skogforsk.se/\" target=\"_blank\">Link&nbsp;</a>to the institute<br /><strong>Short abstract<br /></strong></p><p>Forestry Research Institute of Sweden (Skogforsk stiftelsen skogsbrukets forskningsinstitut) is a private research institute with the mission to pursue R and D aimed at long-term, profitable, and ecologically sound forestry. The institute is supported by the entire forestry industry and the government. One important field of research is human resources and work organisation.</p><p>See more in&nbsp;<a href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Sweden\" target=\"_blank\">OSHWiki</a></p><p><strong>&nbsp;</strong></p><p><strong>&nbsp;</strong></p><p>&nbsp;</p><p><strong>&nbsp;</strong></p><p>&nbsp;</p>");
