INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "The Swedish Agency for Innovation Systems (Vinnova)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_STATISTICS_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "<p><a href=\"http://www.vinnova.se/%20\" target=\"_blank\">Link&nbsp;</a>to the institute<br /><strong>Short abstract<br /></strong></p><p>Vinnova, the Swedish Agency for Innovation Systems (Verket för innovationssystem) finances research and development in the field of innovation systems for a sustainable development of industry and society. Vinnova does not engage in research; its task is to finance projects which will result in work environment improvements.</p><p>See more in&nbsp;<a href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Sweden\" target=\"_blank\">OSHWiki</a></p><p><strong>&nbsp;</strong></p><p><strong>&nbsp;</strong></p><p>&nbsp;</p><p><strong>&nbsp;</strong></p><p>&nbsp;</p>");
