INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "<p>Labour Inspectorate of Estonia</p><p>See more in <a href=\"&lt;a%20data-ui-sref=&quot;OSH-authorities&quot;&gt;Labour-Inspection&lt;/a&gt;\">OSH Authorities</a> in the Labour Inspection section or in the <a href=\"%20http://ti.ee/en/\" target=\"_blank\">website</a></p>");
