INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "The competent authority in occupational safety and health (OSH) matters is the Federal Ministry of Labour, Social Affairs, Health and Consumer Protection. It is responsible for most employment-related issues, namely social security (insurance for pensions and unemployment), industrial relations, labour market policy, labour conditions and inspections, statistics, OSH and vocational training.");
