INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "Labour inspectorate (Arbeitsinpektion)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"_blank\" href=\"https://www.sozialministerium.at/en/Topics/Social-Affairs/People-with-Disabilities/The-participation-in-employment-of-people-with-disabilities.html\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Austria\">OSHwiki</a>.");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "The general labour inspectorate (Arbeitsinspektion) is by far the largest of the bodies that have been established to monitor the safety and health of employees. It covers industries, services, the public sector and transport — following the July 2012 merger with the Inspectorate of Transport. It is centralised and acts nationally through 16 regional departments and one central office. Newly recruited inspectors follow a 2-year training programme, with the emphasis on legislation and general OSH principles. At the level of the federal states, nine regional labour inspectorates for agriculture and forestry have been established.");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id)  VALUES ("MATRIX_AUTHORITY", @nutsId, 1, 0, 0, 0, @maxId+1, @maxId+2, @maxId+3);
