INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"_blank\" href=\"http://www.emploi.belgique.be\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Belgium\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "Occupational safety and health (OSH) is a matter within the competence of the Federal Public Service Employment, Labour and Social Dialogue. The main executive agencies are the Directorate-General for the Humanisation of Work (DG Humanisation du travail — DGHT) and the Directorate-General for the Control of Well-Being at Work (DG Contrôle du bien-être au travail). The Federal Public Service is the responsible administrative body, led by the Federal Minister for Work");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id)  VALUES ("MATRIX_AUTHORITY", @nutsId, 1, 0, 0, 0, @maxId+1, @maxId+2, @maxId+3);
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+1, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "Directorate-General for Control of Well-Being at Work (inspection services)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"_blank\" href=\"http://www.emploi.belgique.be/defaultTab.aspx?id=6550\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Belgium\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "<p>The Directorate-General for the Control of Well-Being at Work is organised into three main divisions: (1) a division for regional control, with eight regional directorates; (2) a division for chemical risk control, with three directorates — one for the prevention of major accidents, one for control policies and one for industrial toxicology; and (3) a division for knowledge management. It covers all topics as defined by the law for well-being at work.</p><p>The task of the inspection services is to help reduce accidents and health problems in businesses and public services, based on the regulations. They ensure that the rules are adhered to and have an advisory, preventive and repressive role. Another of its tasks is indicating all the loopholes in the legislation and helping the authorities to close these.</p>");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id)  VALUES ("MATRIX_AUTHORITY", @nutsId, 1, 0, 0, 0, @maxId+1, @maxId+2, @maxId+3);
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+1, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "Directorate-General for the Humanisation of Work");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"_blank\" href=\"http://www.emploi.belgique.be/defaultTab.aspx?id=332\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Belgium\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "<p>The Directorate-General for the Humanisation of Work is organised into three divisions: (1) a division for the preparation of legislation; (2) a division for the promotion of well-being at work; and (3) a division for social consultation.</p><p>The division for the preparation of legislation draws up the accepted norms regarding well-being on the shop floor and interprets and evaluates these accepted norms. It also deals with the preparation of these accepted norms at European and international levels and the incorporation of the European and international norms into Belgian law.</p><p>The division for promoting well-being at work arranges promotion activities and information regarding well-being at work at an international level and a Belgian level. It develops action programmes on the distribution and use of ‘good practices’ and networks to exchange information on well-being at work.</p><p>With a view to reaching small businesses more effectively, provincial committees promote work in each province. These committees are tripartite committees that consist of representatives of employers, employees and the administration, of which the governors of the provinces are presidents. They carry out regional information campaigns controlled by the Directorate-General for the Humanisation of Work.</p><p>The division for social consultation on well-being at work organises social consultation, among other things, on the level of accepted norms regarding well-being at work. It also manages the procedures for recognition and approval of the external stakeholders involved in well-being at work.</p>");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id)  VALUES ("MATRIX_AUTHORITY", @nutsId, 1, 0, 0, 0, @maxId+1, @maxId+2, @maxId+3);
