INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "The GLI EA is a subordinate of the MLSP. It is in charge of monitoring legal compliance and reporting legislative deficiencies to the MLSP. It provides information and technical advice to both employers and employees. The GLI EA has labour inspection offices in the administrative regions of Bulgaria.");
