INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "Since 2006, notifications about labour contracts, declarations about insured persons and returns about social security contributions due have been filed at the offices of the National Revenue Agency. Returns about the health insurance contributions for persons insured by the state budget, and the returns for social security and health insurance contributions paid in advance are also filed at the Agency.");
