INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "The Croatian Institute for Health Insurance (Hrvatski zavod za zdravstveno osiguranje) is the administrative body for general health insurance, including industrial accident insurance under the ministerial responsibility of the Ministry of Health. From 2006 to 2010, the Croatian Institute for Occupational Health Insurance was responsible for the allocation of funds for prevention activities, diagnosis of and treatment for occupational diseases and accidents, and sick leave compensation. These tasks have been performed by the Croatian Institute for Health Insurance since 2011.");
