INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"_blank\" href=\"http://www.hzzzsr.hr/\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_National_Level_-_Croatia \">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "The CIOH (Hrvatski zavod za zaštitu zdravlja i sigurnost na radu) is a public institute under the ministerial responsibility of the Ministry of Health that is responsible for implementing relevant legislation. Its main objective is to improve the quality of occupational health for all Croatian employees. To this end, the CIOH is involved in defining measures for health protection, developing standards and working methods, and providing advice, supervision and training on occupational health protection. The CIOH is the body responsible for maintaining registers and databases, reporting and monitoring occupational accidents and diseases in Europe, and collecting and analysing data on working conditions and occupational accidents and diseases.");
