INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "The Standardisation, Accreditation and Technical Information Law (156(I)/2002) has allocated standardisation activities to the Cyprus Certification Company, which was renamed CYS. It is the national standardisation body, and it participates in the European and international standardisation process. ‘Mirror Committee 04’ follows international activities in the health and safety sector.");
