INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "Ministry of Health (Ministerstvo zdravotnictví)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"_blank\" href=\"http://www.mzcr.cz/\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Czech_Republic\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "In Czechia, occupational safety and occupational health has traditionally two distinct features. While the Ministry of Labour and Social Affairs (Ministerstvo práce a sociálních věcí) is responsible for occupational safety and labour inspection, the Ministry of Health (Ministerstvo zdravotnictví) is the competent authority for public health, which includes occupational health, and is in charge of occupational health inspections. Although this distinction is reinforced by the executive agencies of the two ministries, they cooperate closely on international, national and regional levels in the field of OSH. The systems of administrative authorities are run from a state budget. Both are authorised to impose sanctions if they find severe deviations during inspection activities.");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id)  VALUES ("MATRIX_AUTHORITY", @nutsId, 1, 0, 0, 0, @maxId+1, @maxId+2, @maxId+3);
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+1, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "State Labour Inspection Office (Státní úřad inspekce práce)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"_blank\" href=\"http://www.suip.cz/\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Czech_Republic\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "Labour inspectorates were established as a separate authority in 2005 by Act No. 251/2005 Coll. on Labour Inspection, based on their predecessor, the Czech Office of Labour Safety. The State Labour Inspection Office and its eight subordinate regional labour inspectorates are concerned with the inspection of compliance with OSH protection, as well as the control of protection of labour relations and working conditions. Employers and employees can take advantage of the seminars and individual consultancy services provided by the central headquarters and the regional inspectorates.");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id)  VALUES ("MATRIX_AUTHORITY", @nutsId, 1, 0, 0, 0, @maxId+1, @maxId+2, @maxId+3);
