INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "Czech Insurance Company (Česká pojišťovna)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"_blank\" href=\"http://www.ceskapojistovna.cz/\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Czech_Republic\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "<p>Currently, Decree No 125/1993 authorises two private insurance companies that provide accident insurance:<br />1. the Czech Insurance Company (Česká pojišťovna);<br />2. Kooperativa Insurance Company (Kooperativa pojišťovna).<br />The system does not cover people who are self-employed.<br />Their activity does not extend to prevention.<br />In the case of Česká pojišťovna, those employers who were covered by this insurance contract with Česká pojišťovna up until 31 December 1992 are insured, as well as employers whose rights and obligations in the employment relationship have been transferred to their legal successor. All other employers are insured with Kooperativa pojišťovny.</p>");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id)  VALUES ("MATRIX_AUTHORITY", @nutsId, 0, 1, 0, 0, @maxId+1, @maxId+2, @maxId+3);
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+1, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "Kooperativa Insurance Company (Kooperativa pojišťovna)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"_blank\" href=\"http://www.koop.cz/\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Czech_Republic\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "<p>Currently, Decree No&nbsp;125/1993 assigns two private insurance companies that provide accident insurance:</p><ol><li>the Czech Insurance Company (<em>Česká pojišťovna</em>)</li><li>Kooperativa Insurance Company (<em>Kooperativa pojišťovna</em>)</li></ol><p>The system does not cover people who are self-employed.</p><p>Their activity does not extend to prevention.</p>");
