INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+1, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "Centre of Occupational Health (Centrum hygieny práce a pracovního lékařství )");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"_blank\" href=\"http://www.szu.cz/centrum-hygieny-prace-a-pracovniho-lekarstvi\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Czech_Republic\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "The mission of the centre is to study the effects of working conditions and occupational risks on the health of workers and act as an expert body for the Ministry of Health in the field of occupational health. It carries out research, provides expert background information for legislation and maintains international cooperation, including in the areas of the health effects of work and working conditions, prevention, health promotion, diagnosis, therapy and medico-legal aspects of occupational and/or other work-related diseases. The centre is an organisational unit of the National Institute of Public Health (Státní zdravotní ústav).");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id)  VALUES ("MATRIX_AUTHORITY", @nutsId, 0, 0, 1, 0, @maxId+1, @maxId+2, @maxId+3);
