INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "<p>AES is an independent institution administrated by the Danish Labour Market Supplementary Pension Scheme (<em>Arbejdsmarkedets Tillægspension</em> — ATP).</p><p>It follows the applicable regulations in determining whether or not an injury or illness can be classified as an industrial injury. When an injury or illness is recognised as an industrial injury, AES then decides the amount of compensation to be paid.</p><p>AES funds and pays compensation for recognised occupational diseases. It is mandatory for all private and public sector employers in Denmark to contribute to AES.</p>");
