INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"_blank\" href=\"http://www.avi.fi/fi/web/avi/tyosuojelu#.Wqos65POUdU\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Finland\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "<p>The inspectorates provide instructions and advice on applying the regulations on OSH matters, working conditions, employment and equality, and ensure that there is adherence to these regulations and guidelines in the workplace.<br />The OSH authorities need to monitor compliance with more than 100 items of legislation. The most important legislative acts are the Occupational Safety and Health Act and the decrees based upon it, the Occupational Health Care Act, the Employment Contracts Act, the Working Hours Act, the Annual Holidays Act and the Young Workers’ Protection Act. The OSH authorities also investigate serious occupational accidents and serious cases of occupational diseases .</p>");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id)  VALUES ("MATRIX_AUTHORITY", @nutsId, 1, 0, 0, 0, @maxId+1, @maxId+2, @maxId+3);
