INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "The Finnish Standards Association (SFS — Suomen Standardoimisliitto SFS ry)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"_blank\" href=\"https://www.sfs.fi/en\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Finland\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "<p>SFS is the central body for standardisation in Finland. Its membership includes the central government and professional, commercial and industrial organisations. It approves national SFS standards and coordinates national standardisation Standardisation is carried out at international, regional, national and company levels. SFS also manages the Nordic and European environmental labelling systems for products and services.</p><p>SFS promotes the use of standards in Finland by disseminating information on international, foreign and Finnish standards and drafts. It is the Finnish source for all international standards and drafts of the International Organisation for Standardisation (ISO), for European standards and drafts of the European Committee for Standardisation (CEN), and for publications of ISO member bodies. SFS also distributes other foreign standards and technical regulations.</p>");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id)  VALUES ("MATRIX_AUTHORITY", @nutsId, 0, 0, 0, 1, @maxId+1, @maxId+2, @maxId+3);
