INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "<p>See more in its <a href=\"https://travail-emploi.gouv.fr/ministere/organisation/article/direccte-directions-regionales-des-entreprises-de-la-concurrence-de-la\" target=\"_blank\">website</a> and in <a href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_France\" target=\"_blank\">OSHwiki</a></p>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "<p>The Ministry of Labour implements government policy in the fields of labour, social dialogue, prevention of occupational accidents and diseases, employment, and vocational training.</p><p>&nbsp;</p><p>The Directorate-General for Labour (<em>Direction générale du travail</em>) is the branch of the Ministry of Labour that prepares, leads, and coordinates labour policy to improve collective and individual relations at work, to support and monitor collective agreements, to improve working conditions in companies, and to protect occupational safety and health (OSH). Given this, it is in charge of drafting and applying legislative and regulatory texts, and developing actions in these fields.</p>");
