INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "Regional directorates for companies, competition, consumption, labour and employment (DIRECCTEs — Directions Régionales des Entreprises, de la Concurrence, de la Consommation, du Travail et de l’Emploi) — labour inspectorates");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"_blank\" href=\"https://travail-emploi.gouv.fr/ministere/organisation/article/direccte-directions-regionales-des-entreprises-de-la-concurrence-de-la \">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_France\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "The labour inspectorate (inspection du travail) services have been attached to the DIRECCTEs since they were set up by Decree 2009-1377 of 10 November 2009. Labour inspection is carried out mainly by labour inspectors and controllers in charge of inspecting companies and informing the public. They make sure that the labour laws are being properly implemented; they advise and inform employers, employees and staff representatives about their rights and obligations; they facilitate out-of-court conciliation, particularly for collective disputes. The Labour Medical Inspectorate (Inspection Médicale du Travail) constitutes a specific technical support body within the labour inspectorate services.");
