INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "French Agricultural Social Security Scheme (MSA — Mutualité Sociale Agricole)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"_blank\" href=\"http://www.msa.fr/lfy\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_France\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "The existence of agricultural mutual benefit societies in France dates back to the 19th century. The role and responsibilities of the MSA were defined by the Orders of 1945, which set up the French social security system. The MSA is the mandatory welfare protection body for farmers and agricultural workers. It covers, in particular, the risks of occupational accidents and diseases in agriculture, landscaping, forestry and agri-food industries. The Agreement on Objectives and Management (COG) defines the mutual commitments of the French State and the MSA. The central fund of the MSA (Caisse Centrale de la MSA) represents the MSA at the national level and leads a network of 35 regional funds. A specific occupational health and security plan for the agriculture sector has been adopted for the period 2016-2020");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id)  VALUES ("MATRIX_AUTHORITY", @nutsId, 0, 1, 0, 0, @maxId+1, @maxId+2, @maxId+3);
