INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"_blank\" href=\"https://www.secu-independants.fr/nous-connaitre.html
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "The RSI took over from three previous existing schemes and was set up pursuant to the provisions of French Law No 2004-1343 of 9 December 2004. It is run by the representatives of its insured parties, who are craftspeople, shopkeepers and self-employed professionals. It performs a public service mission by managing the mandatory welfare protection of over 4 million self-employed enterprise managers and their dependants and beneficiaries under the Agreement on Objectives and Management, signed by the French State and the RSI.");
