INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "There are 16 other special social security schemes, including schemes for personnel of the French railway (Société Nationale des Chemins de Fer), civil servants and state workers, the mining sector, and the electricity and gas industries.");
