INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "German Institute for Standardisation (DIN — Deutsches Institut für Normung e.V.)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"_blank\" href=\"http://www.din.de/\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Germany\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "DIN is the official German standardisation body. This registered non-profit association was acknowledged by an agreement with the German federal government. DIN represents German interests in European and international standards organisations. Its primary task is to work closely with its stakeholders (industry, government, associations) to develop consensus-based national and international standards that meet market requirements.");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id)  VALUES ("MATRIX_AUTHORITY", @nutsId, 0, 0, 0, 1, @maxId+1, @maxId+2, @maxId+3);
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+1, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "Directorate for Safety and Health at Work of the Ministry of Labour, Social Security and Social Solidarity");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"_blank\" href=\"http://www.ypakp.gr\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Greece\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "<p>The&nbsp;Directorate for Safety and Health at Work, which promotes all issues related to occupational safety and health (OSH) (except the inspection and enforcement of national OSH legislation), is the principal OSH administration authority of the Ministry of Labour, Social Security and Social Solidarity. The Directorate is responsible for, among other things, the planning, organisation, implementation, monitoring and evaluation of the national OSH strategy and policies, the national OSH system and the national OSH programme, in accordance with national priorities and the running of the European Union (EU) OSH strategy. Its activities are mainly directed at the fields of OSH legislation, policy-making, decision-making, administration, information management and documentation, awareness raising, networking, education, training and research.</p><p>&nbsp;</p><p>To implement these actions, the aforementioned directorate, with the principal operational objective of planning, monitoring and evaluating actions under the national strategy for safety and health at work, is divided into six departments:</p><ul type=\"square\"><li>Department of Working Conditions;</li><li>Department of Health Promotion, Harmful Factors, Safety and Ergonomics;</li><li>Department of the National Focal Point for OSH Information and Documentation;</li><li>Department for the Major Accidents (under the Seveso Directive);</li><li>Department for OSH Training and Monitoring of Policies on OSH issues;</li><li>Department for People with Disabilities.</li></ul>");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id)  VALUES ("MATRIX_AUTHORITY", @nutsId, 1, 0, 0, 0, @maxId+1, @maxId+2, @maxId+3);
