INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "The MSZT is an independent, non-profit public body. Its main activities and services include standardisation, certification, adult education, issue of standards, distribution of standards and information supply.");
