INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "Personal Injuries Assessment Board");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"_blank\" href=\"https://www.piab.ie\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Ireland\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "Under the Personal Injuries Assessment Board Act 2003, anyone intending to seek compensation for a personal injury (other than a personal injury arising from medical negligence) must make an application through InjuriesBoard.ie. InjuriesBoard.ie is the statutory body that provides an independent assessment of personal injury compensation for victims of accidents, including workplace accidents. InjuriesBoard.ie is staffed with public servants that have the relevant legal, insurance and medical expertise to assess OSH compensation claims, and has established an independent panel made up of medical experts from across Ireland.   Employees use their employer’s public liability insurance to claim for injuries and ill health from work-related accidents and illnesses through the courts for civil compensation. The state also has a system of occupational injury and illness benefits that are paid to those out of work. These are paid from the budget of the Department of Social and Family Protection.");
