INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"_blank\" href=\"https://niso.ie/\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Ireland\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "<p>NISO provides information and services to help improve safety and the workplace. Its services include:<br />- providing leading information, and advisory and training services;<br />- promoting a culture of excellence in workplace health and safety;<br />- playing a leading role in advancing the national health and safety agenda.</p>");
