INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "National Standards Authority of Ireland (NSAI)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"_blank\" href=\"https://www.nsai.ie\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Ireland\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "The NSAI is Ireland’s official standards body. It operates under the National Standards Authority of Ireland Act (1996) and is accountable to the Minister for Business, Jobs, Enterprise and Innovation  . The NSAI is the national certification authority for CE marking, and it provides a certification service to enable businesses to demonstrate that Irish goods and services conform to the applicable standards. As Ireland’s official standards body, the NSAI aims to inspire consumer confidence and create the infrastructure needed for products and services to be recognised and relied upon all over the world.");
