INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "Italian Workers’ Compensation Authority (INAIL — Istituto Nazionale per l’assicurazione contro gli infortuni sul lavoro)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"_blank\" href=\"https://www.inail.it\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Italy\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "INAIL pursues various objectives: to reduce injury occurrence; to provide insurance for workers carrying out high-risk activities; and to guarantee the reintegration of injured workers into working life. In the light of recent legal developments, the system for protecting workers has become an even more integrated system of protection  , including prevention actions in the workplace, health and economic benefits, health care, and rehabilitation and reintegration into social and working life of workers who have suffered physical injuries due to accidents in the workplace or occupational diseases. To reduce accidents, INAIL carries out important actions aimed at monitoring, on an ongoing basis, trends in employment and accidents, provides training and assistance to small and medium-sized enterprises in relation to prevention, and funds companies that are investing in safety improvements.");
