INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"_blank\" href=\"http://www.vsaa.lv/\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_systems_at_national_level_-_Latvia\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "There is only one insurance and compensation body in Latvia, as the social insurance system and accident insurance system is state owned. The system is provided by the State Social Insurance Agency working under the supervision of the Ministry of Welfare. The main functions of the State Social Insurance Agency include the administration of special social insurance budgets (including insurance against accidents at work and occupational diseases) and public social services. The State Social Insurance Agency also provides funding for preventive activities in the field of OSH. The system for insurance against accidents at work and occupational diseases is set by the Law on Compulsory Social Insurance in respect of Accidents at Work and Occupational Diseases. The State Social Insurance Agency is handling the compensation system for occupational accidents and diseases. Generally, every employee that is legally employed is insured against occupational accidents and diseases, as a small part of social tax is paid into the special social insurance budget. If there is an occupational accident, it must be properly investigated and registered with the State Labour Inspectorate in accordance with the Cabinet Regulations ‘Procedures for Investigation and Registration of Accidents at Work’. Occupational diseases are recognised and registered by the Centre of Occupational and Radiation Medicine (Aroda un radiācijas medicīnas centrs) in accordance with the Cabinet Regulations ‘Procedures on Investigation and Registration of Occupational Diseases’.");
