INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "Luxembourg Institute for the Standardisation, Accreditation, Safety and Quality of Products and Services (ILNAS — Institut Luxembourgeois de la Normalisation et de l’Accréditation, de la Sécurité et Qualité des Produits et Services)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"blank\" href=\"https://portail-qualite.public.lu/fr/acteurs/ilnas.html\">website</a> and in <a target=\"blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Luxembourg\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "<p>ILNAS is an administrative body under the supervision of the minister responsible for the economy. It was created on the basis of a law of 20 May 2008 and commenced its activities on 1 June 2008.</p><p>ILNAS carrier out the following activities, among others:<br />- standardisation;<br />- the accreditation of conformity assessment bodies;<br />- the monitoring of general product safety;<br />- the promotion of quality management.</p>");
