INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"blank\" href=\"http://www.aaa.lu/\">website</a> and in <a target=\"blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Luxembourg\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "<p>The AAA is a public institution that is responsible for the prevention of and the payment of compensation for accidents at work and occupational diseases.<br />The organisation is overseen by the Ministry of Social Security and is run by a management committee that consists of an official appointed by the government to chair the committee, eight employers’ delegates and eight employees’ delegates.<br />The AAA provides insurance and pays compensation in the event of accident or illness experienced by workers in both the private and public sectors.<br />The organisation has a prevention service and a payment service (compensation). The reform of accident insurance in 2010 put an end to the compulsory prevention regulations introduced specifically by the organisation, in addition to the legislation on safety and health; these regulations were replaced by recommendations.<br />The role of the AAA’s prevention service is: <br />- to analyse the causes of work-related accidents and illnesses;<br />- to monitor the legal and regulatory requirements with regard to OSH;<br />- to devise prevention recommendations;<br />- to inform and advise employers, and raise awareness;<br />- to propose financial incentives to employers (no claims bonus system, financial support for the management of OSH in companies).</p>");
