INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"blank\" href=\"http://www.rva.nl/home/ \">website</a> and in <a target=\"blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Netherlands\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "In many countries, accreditation is a government task; however, in the Netherlands, the accreditation institute RvA is an organisation governed by private law, through which the government acts as client, regulator and discussion partner. If the government considers it necessary to implement accreditation in particular sectors, the government is the client. However, the government is not only the client, but also a discussion partner for the accreditation institute. The RvA holds regular discussions with the Ministry of Housing, Spatial Planning and the Environment and the Ministry of Social Affairs and Employment. The agreement between the state and the RvA allows room for formulating tailor-made agreements for specific areas of special attention with various ministries.");
