INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"blank\" href=\"https://www.nen.nl\">website</a> and in <a target=\"blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Netherlands\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "NEN supports the making of agreements on products, processes and services, and publishes standards. Employers and employees are mutually responsible for setting their own standards, so NEN supplies them with the knowledge and experience they need for their consultations. NEN works in close collaboration with European partners (CEN — the European Committee for Standardisation, Cenelec — the European Committee for Electrotechnical Standardisation, and ETSI — the European Telecommunications Standards Institute) and global partners (ISO — the International Organisation for Standardisation, IEC — the International Electrotechnical Commission, and ITU — the International Telecommunication Union).");
