INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"blank\" href=\"http://www.inspectieszw.nl/ \">website</a> and in <a target=\"blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Netherlands\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "<p>Compliance with OSH rules and regulations is monitored by the Inspectorate SZW. Since January 2012, the Inspectorate SZW has combined the work organisation and activities of the former Labour Inspectorate, the Work and Income Inspectorate and the Social and Intelligence Investigation Service of the Ministry of Social Affairs and Employment.</p><p>The Inspectorate SZW aims to achieve safe and healthy working conditions for all. The Inspectorate SZW is working according to a vision of an integrated and risk-oriented supervision. The inspection not only focuses on enforcement but also has preventive tasks.</p>");
