INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "Social Insurance Institution (ZUS)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"blank\" href=\"http://www.zus.pl/ \">website</a> and in <a target=\"blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Poland\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "<p>ZUS collects citizens’ social and health insurance contributions and distributes benefits (e.g. pensions, sickness allowance, maternity allowance). It is the dispenser of the Social Insurance Fund (FUS —Fundusz Ubezpieczeń Społecznych), which was established on 1 January 1999, by virtue of the Social Insurance System Act, to perform tasks related to social insurance . The following funds are distinguished within FUS:</p><ul><li>the old-age pension fund, established to finance old-age pensions and public deficit expenditure, to guarantee the pay out of funded pensions;</li><li>the pension fund, established to finance, among other things, incapacity benefits, training benefits, survivors’ pensions, supplements to survivors’ pensions for complete orphans, nursing allowances, funeral grants, etc.;</li><li>the sickness fund, established to finance sickness, maternity, care, compensatory allowances and rehabilitation benefits;</li><li>the accident fund, established to finance occupational accident pensions and allowances, lump-sum compensations, and incapacity benefits as a direct result of occupational accident or disease.</li></ul><p>Compulsory occupational accident insurance covers persons subject to pension insurance, for example employees, freelancers (contractors), members of agricultural production cooperatives, persons running an agriculture-related business and their contractors.</p><p>A percentage rate of the occupational accident insurance contribution is differentiated by individual contribution payers and depends on the occupational risk category as well as the number of persons covered by the work accident insurance.</p>");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id)  VALUES ("MATRIX_AUTHORITY", @nutsId, 0, 1, 0, 0, @maxId+1, @maxId+2, @maxId+3);
