INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "Portuguese Quality Institute (IPQ — Instituto Portugues da Qualidade)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"blank\" href=\"http://www1.ipq.pt/PT/Pages/Homepage.aspx\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Portugal\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "The IPQ is the national organisation that manages and promotes the development of the Portuguese System for Quality (SQP —Sistema Português para a Qualidade), with its three sub-systems — standardisation, metrology and qualification. As a result, the IPQ is Portugal’s representative body in the quality field at international level and closely cooperates with its European counterparts. Within the framework of the SPQ, the IPQ assumes the role of national standardisation body, thus ensuring coordination with European and international standardisation bodies, and supervises the activity of the Central Laboratory of Metrology.");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id)  VALUES ("MATRIX_AUTHORITY", @nutsId, 0, 0, 0, 1, @maxId+1, @maxId+2, @maxId+3);
