INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "Protection Against Occupational Hazards Department (DPRP — Departamento de Protecção contra os Riscos Profissionais)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"blank\" href=\"http://www.seg-social.pt/doenca-profissional\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Portugal\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "<p>The DPRP is the service of the Institute of Social Security (Instituto de Segurança Social) that is nationally responsible for managing the treatment and recovery from illness or a disability arising from occupational hazards. According to Portaria No 135/2012 of 8 May 2012, the objectives of the DPRP at company level are as follows:<br />- to evaluate and determine the disability of the injured person, the functional disturbance or the disease arising from occupational hazards;<br />- to ensure the provision of medical care to treat the disease or disability resulting from occupational hazards;<br />- to pay compensation for a temporary disability and permanent disability pensions;<br />- to promote the clinical recovery and professional reclassification of beneficiaries with an occupational disease;<br />- to promote the placement of rehabilitated workers in occupations compatible with their physical condition and their ability to work.</p>");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id)  VALUES ("MATRIX_AUTHORITY", @nutsId, 0, 1, 0, 0, @maxId+1, @maxId+2, @maxId+3);
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+1, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "Division of Environmental and Occupational Health (Divisão de Saúde Ambiental e Ocupacional) from Directorate-General of Health (Direção Geral de Saúde)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"blank\" href=\"https://www.dgs.pt/\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Portugal\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "The Division of Environmental and Occupational Health is responsible for proposing strategies and specific programmes for evaluation, coordination and collaboration activities in the management of risk to human health in several areas, notably water, built spaces, waste, chemical and biological substances, genetically modified organisms, and ionising and non-ionising radiation; for monitoring technical advice and licensing facilities, equipment, and chemical and biological substances in accordance with the law; for proposing strategies, coordinating activities and implementing programmes within the field of occupational health; for proposing strategies and coordinating programmes and activities to ensure the prevention of accidents.");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id)  VALUES ("MATRIX_AUTHORITY", @nutsId, 1, 0, 0, 0, @maxId+1, @maxId+2, @maxId+3);
